import UIKit
import PayUParamsKit
import PayUUPICoreKit

class PayUVpaAPIHandler: NSObject {
    var handler = PayUHybridVpaAPIHandler()
    // MARK: - Public Methods -
    public func validateVPA(paramDict: NSDictionary,
                            errorCallback: @escaping RCTResponseSenderBlock,
                            successCallback: @escaping RCTResponseSenderBlock) {
        self.handler.validateVPA(paramDict: paramDict as NSDictionary, callback: { json, error in
            guard let json = json else {
                errorCallback([error ?? [:]])
                return
            }
            successCallback([json])
        })
    }
}
